// Helper function to mark parameters as modified if their current value differs from a known default.
// This is a basic example; a more robust solution would fetch actual defaults from ArduPilot's parameter metadata.
export function markModifiedParameters(categories: ParameterCategory[]): ParameterCategory[] {
  return categories.map(category => ({
    ...category,
    subcategories: category.subcategories.map(subcategory => ({
      ...subcategory,
      parameters: subcategory.parameters.map(param => {
        // Very basic check; 'default' in the data is often the current value from param.txt for simplicity here.
        // A real implementation would need a database of true defaults.
        const isModified = param.default !== undefined && param.value !== param.default;
        return { ...param, modified: isModified };
      })
    }))
  }));
}
